function drawAssetLayer(){
	console.log('drawAssetLayer');
	//was hecc-dev-web01.squiz.co.uk, gis.hertfordshire.gov.uk
    //var saltBins = 'https://hecc-dev-web01.squiz.co.uk/webmaps/rest/services/public/saltbins/MapServer'
