    highwaysAssetLayer = new esri.layers.ArcGISDynamicMapServiceLayer(formSpecific.assetLayerURL, {id: formSpecific.assetLayerID});
	highwaysAssetLayer.setVisibleLayers(formSpecific.assetLayerCodes);
	highwaysAssetLayer.setOpacity(0.9);
	esrimap.addLayer(highwaysAssetLayer);
}
