     update_individual_details = false;
    KDF.customdata('update-individual', 'create', true, true, {'customerID':KDF.getVal('customerID'), 'txt_first_name':KDF.getVal('txt_first_name'),'txt_last_name':KDF.getVal('txt_last_name'),'phone':KDF.getVal('phone'),'email':KDF.getVal('email'),'tel_mobile':KDF.getVal('tel_mobile'),'num_p_streetnumber':KDF.getVal('num_p_streetnumber'),'txt_p_streetname':KDF.getVal('txt_p_streetname'),'txt_p_town':KDF.getVal('txt_p_town'),'txt_p_postcode':KDF.getVal('txt_p_postcode')});  
});

