       // console.log($(this).val());
        if(totalItems > maxAmount){
            exceedsLimit = true
            console.log('Exceeded limit! OHOH!!');
        }
      //  console.log(totalItems);
    });
    
    //check to see if there were any items exceeding the limit:
    if (exceedsLimit){
        KDF.showWarning('The maximum number of items that can be collected is 10');
        KDF.hideSection('area_buttons');
        KDF.hideWidget('but_additem_2016-05-13');
    }
    else{
        KDF.hideMessages();
        KDF.showSection('area_buttons');
        KDF.showWidget('but_additem_2016-05-13');
    }
        
    //further, check max number of items and if assessment is needed (computer, computer_monitor, house_clearance)
    $('.itemcollection > tr').each(function(){ 
        console.log("this:");
        console.log($(this));
        itemType = $(this).find('.selecteditem').attr('name');
        console.log("itemType=" + itemType);
        console.log("number of items in this row:" + $(this).find('#quantity').val());
        totalItemsAssess = parseInt($(this).find('#quantity').val()) + totalItemsAssess ;
        console.log("totalItemsAssess in loop: " + totalItemsAssess );
       // totalItemsAssess = $(this).find('#quantity').val();
       // totalItemsAssess = parseInt($(this).val()) + totalItemsAssess;
       
       
        
        //sixOrMoreItems = $(this).html();
       // console.log($(this).html());
   });
   
   console.log("totalItemsasses after loop: " + totalItemsAssess);
   
   if(totalItemsAssess >= 1){
        //KDF.showWidget('html_assess_message');
        //KDF.showWidget('but_next_assess');
        KDF.showWidget('but_pay_now');
   }
   else if(totalItemsAssess < 1){
        KDF.hideWidget('but_pay_now');
        //KDF.showWidget('but_next_collect');      
   }
   
   checkDuplicates();

    
  //  if((sixOrMoreItems.includes('computer') && totalItemsAssess >= maxAmount)||(sixOrMoreItems.includes('computer_monitor') && totalItemsAssess >= maxAmount)||(sixOrMoreItems.includes('house_clearance') && totalItemsAssess >= maxAmount)){
  //      assessLimitReached = true;
        
