 //   }
}

function initAddItemToCollection(){
	$('#dform_widget_button_but_additem_2016-05-13').click(function(){
		var numberOfAllowedItems = 10;
		if(!(countItems('2016-05-13') > numberOfAllowedItems)){
		    if ($('#dform_widget_sel_itemlist_2016-05-13').val() !== null){
				var selectedlabel = $('#dform_widget_sel_itemlist_2016-05-13 option:selected').text();
				var selectedvalue = $('#dform_widget_sel_itemlist_2016-05-13').val();
				collectionAddRow('2016-05-13', selectedlabel, selectedvalue);
                checkTable();
                checkDuplicates();
                checkTableAmount();
			}
		}
		else{
		    alert('dis tew much');
		    //KDF.showWidget('html_limit_message');
		    KDF.showWarning('The maximum number of items that can be collected is ' + numberOfAllowedItems );

		}
	});
}

function countItems(dateid) {
	return $('.selecteditem.' + dateid).length;
}

function collectionAddRow(dateid, itemlabel, itemvalue) {
    var html = '<tr>';
	html += '<td name="' + itemvalue + '" class="selecteditem ' + dateid + '">' + itemlabel + '</td>';
	html += '<td style="width:100px; text-align:left;"><input id=\"quantity\" type=\"number\" value=\"1\" max=\"10\" min=\"1\"></td>';
	html += '<td style="width:100px; text-align:left;" id=\"subtotal\">£5</td>'
	html += '<td style="width:100px; text-align:left;"><button type="button" class="removeitem">Remove</button></td>';
	html += '</tr>';
    
    var html_header ='<td class="td_header"><strong>Item</strong></td>';
    html_header += '<td class="td_header"><strong>Quantity</strong></td>';
    html_header += '<td class="td_header"><strong>Subtotal</strong></td>';
    
    var html_footer ='<td style="border:none"  class="td_footer"><strong>Total price</strong></td>';
    html_footer += '<td style="border:none"  class="td_footer"><strong></strong></td>';
    html_footer += '<td style="border:none"  class="td_footer" id=\"price_total\"><strong>£0</strong></td>';
    
    console.log('count remove nya nih' +countItems(dateid));
    
    if ( countItems(dateid) === 0 ) {
         $('.table_header').append(html_header);
         $('.table_footer').append(html_footer);
    }
    
	$('.itemcollection.' + dateid).append(html);
	initRemoveItemFromCollection();
    calculatingPrice();
    checkTableAmount();
    checkDuplicates();
}

function initRemoveItemFromCollection(dateid){
	$('.removeitem').off('click').click(function(){
	    //var row_html = $(this).closest('tr').html();
	    console.log('remove nih');
	    $(this).closest('tr').remove();
		KDF.hideMessages();
		checkTable();
		checkTableAmount();
		console.log('countItems : '+countItems('2016-05-13'));
		if(countItems('2016-05-13') == '0'){
		    KDF.hideWidget('but_next_collect ');
            KDF.hideWidget('but_next_assess');
            $(".td_header").remove();
            $(".td_footer").remove();
		}
		calculatingPrice();
		//checkDuplicates();
	});
}

function checkTable(dateid){
    //display message if one or more item in the list needs assessed
    var needsAssessment = false;
    var other = false;
    var fridgeFreezer = false;
    $('table tr').each(function (index){ 
        var tempHtml = $(this).html();
        if(tempHtml.includes('boiler')||tempHtml.includes('bricks')||tempHtml.includes('construction_waste')||tempHtml.includes('fencing')||tempHtml.includes('garden_waste')||tempHtml.includes('wardrobes')||tempHtml.includes('slabs')||tempHtml.includes('photocopiers')||tempHtml.includes('play_frame')||tempHtml.includes('play_slide')||tempHtml.includes('play_swing')||tempHtml.includes('refuse_sacks')||tempHtml.includes('rubble')||tempHtml.includes('shrubs_trees')||tempHtml.includes('soil')||tempHtml.includes('tiles'))
        {
            needsAssessment = true;
        }
        if(tempHtml.includes('other'))
        {
            other = true;
        }
        if(tempHtml.includes('freezer_fridge'))
        {
            fridgeFreezer = true;
        }
    });
    console.log(needsAssessment);
    //if true, then show assessment button and message
    if(needsAssessment == true)
    {
        KDF.showWidget('html_assess_message');
        KDF.showWidget('but_next_assess');
        KDF.hideWidget('but_next_collect');
        KDF.hidePage('collection_point');
        KDF.hidePage('page_request_time');
    }
    
    //if false, then show collection button
    else if(needsAssessment == false)
    {
        KDF.hideWidget('html_assess_message');
        KDF.showWidget('but_next_collect');
        KDF.hideWidget('but_next_assess');
        KDF.showPage('collection_point');
        KDF.showPage('page_request_time');
    }
    if(other == true)
    {
        KDF.showWidget('txta_other_desc');
        KDF.showWidget('html_addinfo_count');

    }
    else if(other == false)
    {
        KDF.hideWidget('txta_other_desc');
        KDF.hideWidget('html_addinfo_count');
    }
    if(fridgeFreezer == true)
    {
        KDF.showWidget('html_fridge_message');
    }
    else if(fridgeFreezer == false)
    {
        KDF.hideWidget('html_fridge_message');
    }
}

function checkDuplicates(dateid)
{
    var contents = {},
    duplicates = false;
    /*$('table tr').each(function() {
        var trContent = $(this).text();
        console.log('trContent : '+trContent);
        if (contents[trContent]) {
            duplicates = true;
            return false;
        }
        contents[trContent] = true;
    });*/
    
    $('.itemcollection > tr').each(function(){ 
        var item = $(this).find('.selecteditem').text();
        console.log("item=" + item);
        if (contents[item]) {
            duplicates = true;
            return false;
        }
        contents[item] = true;
   });
    
    if (duplicates) {
        KDF.showWarning('There were duplicates items.');
        KDF.hideWidget('but_pay_now');
        KDF.hideWidget('but_additem_2016-05-13');
    }
}

