function calculatingPrice() {
        var total_price = 0;
    $('.itemcollection > tr').each(function(){ 
        console.log("this:");
        var tds = $(this).find("td[id='subtotal']").html();
        console.log($(this));
        itemType = $(this).find('.selecteditem').attr('name');
        console.log("itemType=" + itemType);
        console.log("number of items in this row:" + $(this).find('#quantity').val());
        var subtotal_item = $(this).find('#quantity').val();
        var subtotal_price = parseInt(subtotal_item) * 5; 
        $(this).find("td[id='subtotal']").text('£'+subtotal_price);
        total_price += subtotal_price;
        console.log('total price : '+total_price);
        //$(this).find("td[id='price_total']").text('£'+total_price);
   });
   console.log('total price all : '+$("#price_total").html());
   $("#price_total").html('<strong>£'+ total_price +'</strong>');
}

var update_individual_result =false;
var create_individual_result =false;
var update_individual_details = false;
var hideclass = 'fileuploadobject';

// Jquery event delegate so when the confirm location button clicked it can call another function

$('#dform_edinburgh_complaint').off('_KDF_ready').on('_KDF_ready', function(event, kdf) {
    /*KS: entire code for adding gov.uk styles and also for applying default styles*/
    defaultNewStyle(["all","rad-margin-50","mchk-margin-50","mchk-text-align-right","rad-text-align-right","search-no-results"]);
    applyNewStyle();
    
    /*limit uploaded files up to 5*/
    //limitFileUpload('file_upload','5');
    
    /*hide navigation and controls */
    
    if (kdf.params.customerid && kdf.params.interactionid) {
        KDF.hideWidget('cs_customer_search');
        showCustomerDetails();
        console.log('show cust details');
        KDF.setVal('customerID',kdf.params.customerid);
        console.log('customerID :'+KDF.getVal('customerID'));
    }
    
    $('#dform_edinburgh_complaint').off('click', '.copyaddressvalue').on('click', '.copyaddressvalue', function() {
        console.log ('accessed by : '+KDF.access);
            console.log('retrieved value for address : '+KDF.getVal('txt_r_streetnumber') +' '+ KDF.getVal('txt_r_streetname') +' '+ KDF.getVal('txt_r_town') +' '+ KDF.getVal('txt_r_postcode'));
            
            KDF.setVal('num_p_streetnumber',KDF.getVal('txt_r_streetnumber'));
            KDF.setVal('txt_p_streetname',KDF.getVal('txt_r_streetname'));    
            KDF.setVal('txt_p_town',KDF.getVal('txt_r_town'));    
            KDF.setVal('txt_p_postcode',KDF.getVal('txt_r_postcode'));
  
            KDF.gotoNextPage();
    });
    
    if ( kdf.access === 'agent' ) {
        if ( KDF.getVal('rad_viewmode') === 'C' ) {
            console.log('hide');
            KDF.hideNav();
            KDF.hideControls();
            /*KDF.hidePage('page_any_pictures');
            KDF.hidePage('citizen_details');
            KDF.hidePage('citizen_address');
            KDF.hidePage('provide_details_citizen_option');*/
            KDF.showPage('page_customer_provide_details');
            KDF.showPage('provide_details');
            KDF.showPage('provide_details_address');
            //KDF.showPage('create_individual_details');
            //KDF.showPage('create_individual_address');
            
            
        } else if ( KDF.getVal('rad_viewmode') === 'R' ) {
            console.log('show');
            KDF.showNav();
            KDF.showControls();
            KDF.showPage('resolution_inspection');
            KDF.showPage('resolution_repair');
            KDF.showPage('resolution_reinspect');
            KDF.showSection('area_customer_name');
            KDF.showSection('area_customer_contacts');
            KDF.showWidget('txt_c_addressnumber');
            KDF.showWidget('txt_c_addressline1');
            KDF.showWidget('txt_c_town');
            KDF.showWidget('txt_c_postcode');
            
            KDF.hideWidget('but_continue_road_pavement');
            KDF.hideWidget('but_continue_request_details');
            KDF.hideWidget('but_continue_pavement_details');
            KDF.hideWidget('but_continue_any_picture');
            KDF.hideWidget('but_continue_customerprovide');
            KDF.hideWidget('but_continue_customer_address');
            KDF.hideWidget('but_continue_citizen_details');
            KDF.hideWidget('but_details_next');
            KDF.hideWidget('but_continue_your_address');
            
            KDF.hideWidget('but_back_road_pavement');
            KDF.hideWidget('but_back_road_details');
            KDF.hideWidget('but_back_pavement_details');
            KDF.hideWidget('but_back_any_picture');
            KDF.hideWidget('but_back_agent_details');
            KDF.hideWidget('but_back_customer_details');
            KDF.hideWidget('but_back_customer_address');
            KDF.hideWidget('but_back_citizen_provide');
            KDF.hideWidget('but_back_create_individual');
            KDF.hideWidget('but_back_individual_address');
            KDF.hideWidget('but_back_your_details');
            KDF.hideWidget('but_back_your_details_address');
        } else if ( KDF.getVal('rad_viewmode') === 'U' ) {
            KDF.showNav();
            KDF.showControls();
            KDF.showPage('resolution_inspection');
            KDF.showPage('resolution_repair');
            KDF.showPage('resolution_reinspect');
        } else {
            KDF.hideNav();
            KDF.hideControls();
            KDF.showPage('page_customer_provide_details');
            KDF.showPage('provide_details');
            KDF.showPage('provide_details_address');
        }
        
    } else if ( kdf.access === 'citizen' ) {
            console.log('some agent pages are closed');
            //KDF.showPage('page_any_pictures');
            KDF.showPage('citizen_details');
            KDF.showPage('citizen_address');
            KDF.showPage('provide_details_citizen_option');
            KDF.hideNav();
            KDF.hideControls();
            
            /*KDF.hidePage('provide_details_citizen_option');
            KDF.hidePage('provide_details');
            KDF.hidePage('provide_details_address');
            KDF.hidePage('create_individual_details');
            KDF.hidePage('create_individual_address');*/
            
    }

});  // End of KDF_ready


$('#dform_edinburgh_complaint').off('_KDF_custom').on('_KDF_custom', function( event, kdf, response, action) {
    /* action performed when CA  create-indivudal-new is called */
    console.log('action :'+action);
    console.log('response :'+response.data.txt_customerID);
    
	if (action == 'create-individual-new') {
        setCreateIndividualResult(false);
        
        if (response.data.txt_customerID !== undefined) {
            setCreateIndividualResult(true);
            KDF.showSuccess('Individual created successfully');
            console.log('txt_customerID '+response.data.txt_customerID);
        }
	}
	/* action performed when CA  update-indivudal is called */
	else if (action == 'update-individual') {
	    setUpdateIndividualResult(false);
	    console.log ('update result :'+response.data.txt_success);
	    if ( response.data.txt_success === 'Completed' ) {
	        setUpdateIndividualResult(true);
	        KDF.showSuccess('Individual updated successfully');
	    }
	    
	}
});	 // End of KDF_custom

$('#dform_edinburgh_complaint').off('_KDF_optionSelected').on('_KDF_optionSelected', function(event, kdf, field, label, val) {
	// fires when an option has been selected in a dynamic radio/select/multicheckbox widget
	console.log('Viewmode : '+KDF.getVal('rad_viewmode'));
    if (KDF.getVal('rad_viewmode') === 'R') {
        console.log('show');
        KDF.showNav();
        KDF.showControls();
        KDF.showSection('area_customer_name');
        KDF.showSection('area_customer_contacts');
        KDF.showWidget('txt_c_addressnumber');
        KDF.showWidget('txt_c_addressline1');
        KDF.showWidget('txt_c_town');
        KDF.showWidget('txt_c_postcode');
        
        KDF.hideWidget('but_continue_road_pavement');
        KDF.hideWidget('but_continue_request_details');
        KDF.hideWidget('but_continue_pavement_details');
        KDF.hideWidget('but_continue_any_picture');
        KDF.hideWidget('but_continue_customerprovide');
        KDF.hideWidget('but_continue_customer_address');
        KDF.hideWidget('but_continue_citizen_details');
        KDF.hideWidget('but_details_next');
        KDF.hideWidget('but_continue_your_address');
        
        KDF.hideWidget('but_back_road_pavement');
        KDF.hideWidget('but_back_road_details');
        KDF.hideWidget('but_back_pavement_details');
        KDF.hideWidget('but_back_any_picture');
        KDF.hideWidget('but_back_agent_details');
        KDF.hideWidget('but_back_customer_details');
        KDF.hideWidget('but_back_customer_address');
        KDF.hideWidget('but_back_citizen_provide');
        KDF.hideWidget('but_back_create_individual');
        KDF.hideWidget('but_back_individual_address');
        KDF.hideWidget('but_back_your_details');
        KDF.hideWidget('but_back_your_details_address');
        
        
    } else if (KDF.getVal('rad_viewmode') === 'T') {
        KDF.showNav();
        KDF.showControls();
    }else {
        console.log('hide');
        KDF.hideNav();
        KDF.hideControls();
    }
	
	/* Identify when customer approve / disapprove their personal details are associated with the case (agent perspectve) */
    if (field === 'rad_identify'){
         if (label == 'Yes') {
             KDF.showPage('provide_details');
             KDF.showPage('provide_details_address');
             KDF.showPage('create_individual_details');
             KDF.showPage('create_individual_address');
         } else if (label == 'No') {
             KDF.hidePage('provide_details');
             KDF.hidePage('provide_details_address');
             KDF.hidePage('create_individual_details');
             KDF.hidePage('create_individual_address');
         }
    }
    
    if (field === 'rad_identifyc'){
         if (label == 'Yes') {
            KDF.showPage('citizen_details');
            KDF.showPage('citizen_address');
         } else if (field === 'rad_identifyc') {
            KDF.hidePage('citizen_details');
            KDF.hidePage('citizen_address');
         }
    } 
    
    /*show or hide widget when label of CS is changed*/
    if (field == 'cs_customer_search_id'){
        console.log('label now : '+label);
        console.log('value now : '+val);
        if(label == '') {
            hideCustomerDetails();
        } else {
            KDF.setVal('customerID',val);
            showCustomerDetails();;
        }
    } 
    
    /* show or hide address widget when label of PS is changed */
    if (field == 'ps_individual_id') {
        if(label == '') {
            KDF.hideWidget('txt_c_addressnumber');
            KDF.hideWidget('txt_c_addressline1');
            KDF.hideWidget('txt_c_town');
            KDF.hideWidget('txt_c_postcode');
            KDF.hideWidget('but_continue_individual_address');
        } else {
            KDF.showWidget('txt_c_addressnumber');
            KDF.showWidget('txt_c_addressline1');
            KDF.showWidget('txt_c_town');
            KDF.showWidget('txt_c_postcode');
            KDF.showWidget('but_continue_individual_address');
        }
    }
    
}); // End of KDF_optionSelected


$('#dform_xx_report_graffiti').off('_KDF_objectdataLoaded').on('_KDF_objectdataLoaded', function(event, kdf, response, type, id) {
	// fires when the object data has been loaded
	console.log('object data loaded id :'+id);
	console.log('object data loaded response :'+response);
	console.log('object data loaded type :'+type);
	
	if (type === 'customer') {
	    KDF.setVal('num_p_streetnumber',KDF.getVal('txt_r_streetnumber'));
        KDF.setVal('txt_p_streetname',KDF.getVal('txt_r_streetname'));    
        KDF.setVal('txt_p_town',KDF.getVal('txt_r_town'));    
        KDF.setVal('txt_p_postcode',KDF.getVal('txt_r_postcode')); 
        console.log('current value of customer address (retrieved): ' + KDF.getVal('num_r_streetnumber') + KDF.getVal('txt_r_streetname') + KDF.getVal('txt_r_town') + KDF.getVal('txt_r_postcode'));
        console.log('current value of customer address : ' + KDF.getVal('num_p_streetnumber') + KDF.getVal('txt_p_streetname') + KDF.getVal('txt_p_town') + KDF.getVal('txt_p_postcode'));
	}
});

// End of KDF_mapClicked

