/*Process happens when continue button in customer details - address is clicked*/
$('#dform_widget_button_but_continue_customer_address').click(function(){
    console.log('updating individual details');
     update_individual_details = false;
    KDF.customdata('update-individual', 'create', true, true, {'customerID':KDF.getVal('customerID'), 'txt_first_name':KDF.getVal('txt_first_name'),'txt_last_name':KDF.getVal('txt_last_name'),'phone':KDF.getVal('phone'),'email':KDF.getVal('email'),'tel_mobile':KDF.getVal('tel_mobile'),'num_p_streetnumber':KDF.getVal('num_p_streetnumber'),'txt_p_streetname':KDF.getVal('txt_p_streetname'),'txt_p_town':KDF.getVal('txt_p_town'),'txt_p_postcode':KDF.getVal('txt_p_postcode')});  
});

/*update customer details when Update Details button in page customer details is clicked*/
$('#dform_widget_button_but_updatedetails').click(function(){
    console.log('updating individual details');
    update_individual_details = true;
    KDF.customdata('update-individual', 'create', true, true, {'customerID':KDF.getVal('customerID'), 'txt_first_name':KDF.getVal('txt_first_name'),'txt_last_name':KDF.getVal('txt_last_name'),'phone':KDF.getVal('phone'),'email':KDF.getVal('email'),'tel_mobile':KDF.getVal('tel_mobile'),'num_p_streetnumber':KDF.getVal('num_r_streetnumber'),'txt_p_streetname':KDF.getVal('txt_r_streetname'),'txt_p_town':KDF.getVal('txt_r_town'),'txt_p_postcode':KDF.getVal('txt_r_postcode')});  
});

