$('#dform_xx_report_graffiti').off('_KDF_ready').on('_KDF_ready', function(event, kdf) {
	// fires when the form is ready
	
	/*Apply Keaton's Style*/
	defaultNewStyle(["all","rad-margin-50","mchk-margin-50","mchk-text-align-right","rad-text-align-right","search-no-results"]);//KS: see 'Non-recommended defaults' within 'defaultNewStyle(elements)' for optional defaults
    applyNewStyle();
    
    /*Hide navigation and control*/
    KDF.hideNav();
    KDF.hideControls();
    
    /*limit uploaded files up to 5 */
    //limitFileUpload('file_upload','5');
    
    customerid=kdf.params.customerid;
    console.log('customerid : '+customerid);
    
    /* Duplicate value from customer details to customer detials address */
    $('#dform_xx_report_graffiti').off('click', '.copyaddressvalue').on('click', '.copyaddressvalue', function() {
        console.log ('accessed by : '+KDF.access);
            console.log('retrieved value for address : '+KDF.getVal('txt_r_streetnumber') +' '+ KDF.getVal('txt_r_streetname') +' '+ KDF.getVal('txt_r_town') +' '+ KDF.getVal('txt_r_postcode'));
            
            KDF.setVal('num_p_streetnumber',KDF.getVal('txt_r_streetnumber'));
            KDF.setVal('txt_p_streetname',KDF.getVal('txt_r_streetname'));    
            KDF.setVal('txt_p_town',KDF.getVal('txt_r_town'));    
            KDF.setVal('txt_p_postcode',KDF.getVal('txt_r_postcode'));
  
            KDF.gotoNextPage();
    });
});

