    });
    
    // Only show the Map page if we are new into the formmaps
    if (KDF.getVal('txt_caseid')!== 'notset' ) KDF.hidePage('page_location');

});  // End of KDF_ready


$('#dform_xx_shell_pothole').off('_KDF_custom').on('_KDF_custom', function( event, kdf, response, action) {
    /* action performed when CA  create-indivudal-new is called */
    console.log('action :'+action);
    console.log('response :'+response.data.txt_customerID);
    
	if (action == 'create-individual-new') {
        setCreateIndividualResult(false);
        
        if (response.data.txt_customerID !== undefined) {
            setCreateIndividualResult(true);
            KDF.showSuccess('Individual created successfully');
            console.log('txt_customerID '+response.data.txt_customerID);
        }
	}
	/* action performed when CA  update-indivudal is called */
	else if (action == 'update-individual') {
	    setUpdateIndividualResult(false);
	    console.log ('update result :'+response.data.txt_success);
	    if ( response.data.txt_success === 'Completed' ) {
	        setUpdateIndividualResult(true);
	        KDF.showSuccess('Individual updated successfully');
	    }
	    
	}
});	 // End of KDF_custom

$('#dform_xx_shell_pothole').off('_KDF_optionSelected').on('_KDF_optionSelected', function(event, kdf, field, label, val) {
	// fires when an option has been selected in a dynamic radio/select/multicheckbox widget
	console.log('Viewmode : '+KDF.getVal('rad_viewmode'));
    if (KDF.getVal('rad_viewmode') === 'R') {
        console.log('show');
        KDF.showNav();
        KDF.showControls();
        KDF.showSection('area_customer_name');
        KDF.showSection('area_customer_contacts');
        KDF.showWidget('txt_c_addressnumber');
        KDF.showWidget('txt_c_addressline1');
        KDF.showWidget('txt_c_town');
        KDF.showWidget('txt_c_postcode');
        
        KDF.hideWidget('but_continue_road_pavement');
        KDF.hideWidget('but_continue_request_details');
        KDF.hideWidget('but_continue_pavement_details');
        KDF.hideWidget('but_continue_any_picture');
        KDF.hideWidget('but_continue_customerprovide');
        KDF.hideWidget('but_continue_customer_address');
        KDF.hideWidget('but_continue_citizen_details');
        KDF.hideWidget('but_details_next');
        KDF.hideWidget('but_continue_your_address');
        
        KDF.hideWidget('but_back_road_pavement');
        KDF.hideWidget('but_back_road_details');
        KDF.hideWidget('but_back_pavement_details');
        KDF.hideWidget('but_back_any_picture');
        KDF.hideWidget('but_back_agent_details');
        KDF.hideWidget('but_back_customer_details');
        KDF.hideWidget('but_back_customer_address');
        KDF.hideWidget('but_back_citizen_provide');
        KDF.hideWidget('but_back_create_individual');
        KDF.hideWidget('but_back_individual_address');
        KDF.hideWidget('but_back_your_details');
        KDF.hideWidget('but_back_your_details_address');
        
        
    } else if (KDF.getVal('rad_viewmode') === 'T') {
        KDF.showNav();
        KDF.showControls();
    }else {
        console.log('hide');
        KDF.hideNav();
        KDF.hideControls();
    }
	
	/* Identify when customer approve / disapprove their personal details are associated with the case (agent perspectve) */
    if (field === 'rad_identify'){
         if (label == 'Yes') {
             KDF.showPage('provide_details');
             KDF.showPage('provide_details_address');
             KDF.showPage('create_individual_details');
             KDF.showPage('create_individual_address');
         } else if (label == 'No') {
             KDF.hidePage('provide_details');
             KDF.hidePage('provide_details_address');
             KDF.hidePage('create_individual_details');
             KDF.hidePage('create_individual_address');
         }
    }
    
    if (field === 'rad_identifyc'){
         if (label == 'Yes') {
            KDF.showPage('citizen_details');
            KDF.showPage('citizen_address');
         } else if (field === 'rad_identifyc') {
            KDF.hidePage('citizen_details');
            KDF.hidePage('citizen_address');
         }
    } 
    
    /* This is the road/pavement radio button */
    if (field === 'le_eventcode') {
      
        if (label === 'Road') {
            KDF.setVal('txt_pin_subset','PH:');
            KDF.setVal('typeofissue','road defect');
            console.log('road label');
            KDF.showPage('page_request_details_road');
            KDF.hidePage('page_request_details_pavement');
            
        }
        else if (label === 'Pavement') 
        {
            KDF.setVal('txt_pin_subset','PV:');
            KDF.setVal('typeofissue','pavement defect');
            console.log('pavement label');
            KDF.hidePage('page_request_details_road');
            KDF.showPage('page_request_details_pavement');
        }
    }
    
    /*show or hide widget when label of CS is changed*/
    if (field == 'cs_customer_search_id'){
        console.log('label now : '+label);
        console.log('value now : '+val);
        if(label == '') {
            KDF.hideSection('area_customer_name');
            KDF.hideSection('area_customer_contacts');
            KDF.hideWidget('hrd_customerdetails');
            KDF.hideWidget('but_updatedetails');
            KDF.hideWidget('but_continue_selectindividual');
            KDF.hideWidget('but_individual_address');
            
        } else {
            KDF.setVal('customerID',val);
            KDF.showSection('area_customer_name');
            KDF.showSection('area_customer_contacts');
            KDF.showWidget('but_continue_selectindividual');
            KDF.showWidget('but_individual_address');
            KDF.showWidget('hrd_customerdetails');
            KDF.showWidget('but_updatedetails');
            console.log ('txt_customerID :'+KDF.getVal('txt_customerID'));
        }
    } 
    
    /* show or hide address widget when label of PS is changed */
    if (field == 'ps_individual_id') {
        if(label == '') {
            KDF.hideWidget('txt_c_addressnumber');
            KDF.hideWidget('txt_c_addressline1');
            KDF.hideWidget('txt_c_town');
            KDF.hideWidget('txt_c_postcode');
            KDF.hideWidget('but_continue_individual_address');
        } else {
            KDF.showWidget('txt_c_addressnumber');
            KDF.showWidget('txt_c_addressline1');
            KDF.showWidget('txt_c_town');
            KDF.showWidget('txt_c_postcode');
            KDF.showWidget('but_continue_individual_address');
        }
    }
    
}); // End of KDF_optionSelected


